/* @Props
DPP_STAB_ColumnNames="Batch Number", "Stab Storage Condition", "Protocol Months On Stability", "Parameter Field Name", "Actual Days On Stability", "Actual Months On Stability", "Actual Weeks On Stability", "Actual Years On Stability", "Assay Method", "Assay Name", "Chart Image Location", "Date Parameter Value", "Date of Manufacture", "Days On Stability since DOM", "Grouping Parameter", "Last Modification Date", "Manufacture Company", "Manufacture Site", "Material Number", "Months On Stability since DOM", "Numeric Parameter Value", "Parameter Category", "Parameter Entry Qualifier", "Parameter Units", "Parameter _w_Units_ Field Name", "Process Variation", "Product Category 1 Name", "Product Category 1 Value", "Product Category 2 Name", "Product Category 2 Value", "Product Category 3 Name", "Product Category 3 Value", "Product Name", "Project Name", "Project Status", "Protocol Days On Stability", "Protocol Name", "Protocol Template", "Protocol Test Article", "Protocol Weeks On Stability", "Protocol Years On Stability", "Result ID", "Result Number", "Sample Number", "Sample Start Date", "Spec Max Value", "Spec Min Value", "Spec Rule", "Spec Text Value", "Stab Storage Orientation", "Stability Base Date", "Sub Product Name", "Test Review Date", "Test Start Date", "Testing Target Date", "Testing Time Point", "Text Parameter Value", "Validation State", "Weeks On Stability since DOM", "Worklist Name", "Years On Stability since DOM", "Investigation", "Serotype", "Suite"
DPP_STAB_UsedColumns="Parameter Field Name","Assay Name","Numeric Parameter Value","Parameter Units","Spec Max Value","Spec Min Value","Spec Rule","Spec Text Value","Text Parameter Value"
DPP_STAB_AssayNamesColIndex=9
DPP_STAB_FieldNamesColIndex=3

